'use strict';

var tty = require('tty');



var iswin = process.platform === "win32";
var isatty = tty.isatty(1) && tty.isatty(2);


var getConsoleWidth = exports.getConsoleWidth = function() {
  if (!isatty) {
    return 80;
  }

  return process.stdout.getWindowSize ? process.stdout.getWindowSize(1)[0] : tty.getWindowSize()[1];
}


var _specialChars = {
  def: {
    tick: '✓',                  // \u2713
    cross: '✖',                 // \u2716
    dot: '․'                  // \u2024
  },

  win: {
    tick: '\u221A',           // √
    cross: '\u00D7',          // ×
    dot: '.'
  }
};



var getSpecialChars = exports.getSpecialChars = function(k) {
  return _specialChars[k || iswin ? 'win' : 'def'];
}


